function showAccordian(){
   const accordianEl = document.querySelector(".accordian");

   accordianEl.addEventListener('click', (e) =>{
       if (!e.target.classList.contains('.accordian_title')){
           return;
       }
       const itemEl = e.target.closest('.accordian_item');
       itemEl.classList.toggle('.accordian_item_open');
   })
};

export default showAccordian;
