import './ThemeSwitcher.css';

function ThemeSwitcher({ className, theme, toggleTheme}){
    return(
        <span className={`${className} theme-switcher`}>
            <label htmlFor="mode">Change Mode:</label>
            <button id='mode' type='button' className="toggle" onClick={toggleTheme}>{theme}</button>
        </span>
    );
}

export default ThemeSwitcher;
